import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import './index.css';
import { RandomColor } from "./styles.js";
import Header from './components/Header.jsx';
import Footer from './components/Footer.jsx';
import { TiLocationArrowOutline } from "react-icons/ti";
import { CiCloudOn, CiCloudDrizzle, CiSun } from "react-icons/ci";

/**
 * Helper: safe icon mapping
 */
const getWeatherIcon = (desc = "") => {
  const d = (desc || "").toLowerCase();
  if (d.includes("rain")) return <CiCloudDrizzle size={20} />;
  if (d.includes("cloud")) return <CiCloudOn size={20} />;
  if (d.includes("sun")) return <CiSun size={20} />;
  return <CiCloudOn size={20} />;
};

/**
 * Card renderer (unchanged layout, but uses stable city.color)
 */
function RenderingArrayOfCityObjects({ cityData = [], onRemove, toggleFavorite }) {
  const citylistItems = cityData.map((city, index) => {
    const cardColor = city.color || RandomColor(); // fallback

    // Mock 3-day forecast using available values (safe fallbacks)
    const minT = typeof city.minTemperature === 'number' ? city.minTemperature : (city.temperature ?? 0) - 2;
    const maxT = typeof city.maxTemperature === 'number' ? city.maxTemperature : (city.temperature ?? 0) + 2;

    const forecast = Array(3).fill().map((_, i) => ({
      day: `Day ${i + 1}`,
      tempMin: minT - i,
      tempMax: maxT + i,
      icon: getWeatherIcon(city.description)
    }));

    return (
      <div className="card animate-card" key={`${city.cityName || 'city'}-${index}`}>
        <div className="top-division" style={{ background: cardColor }}>
          <div className='top-leftDivision'>
            <h2 className='city-name'>{city.cityName ?? city.city ?? "Unknown"},{city.country ?? ""}</h2>
            <p className='city-time'>{city.lastUpdatedTime ?? city.lastUpdated ?? ""}</p>
            <div className='weather-desc'>
              {getWeatherIcon(city.description)}
              <p className='weather-description'>{city.description ?? city.weather ?? "No description"}</p>
            </div>
          </div>
          <div className='top-rightDivision'>
            <div className='temp-content'>
              <h1 className='temp'>{(city.temperature ?? city.temp ?? "--")}°C</h1>
              <p className='temp-min'>Min: {minT}°C</p>
              <p className='temp-max'>Max: {maxT}°C</p>
            </div>
          </div>
        </div>

        <div className="bottom-division">
          <div className='bottom-content'>
            <div className="extra-details">
              <p className='pressure'>Pressure : {city.pressure ?? city.press ?? "--"} hPa</p>
              <p className='humidity'>Humidity : {city.humidity ?? "--"}%</p>
              <p className='visibility'>Visibility : {city.visibility ?? "--"}</p>
            </div>
            <div className="wind">
              <div className='wind-content'>
                <div className='wind-icon'><TiLocationArrowOutline /></div>
                <div><p className='wind-speed'>{city.wind ?? city.wind_speed ?? "--"}</p></div>
              </div>
            </div>
            <div className="sun">
              <p className='sun-rise'>Sunrise : {city.sunrise ?? "--"}</p>
              <p className='sun-set'>Sunset : {city.sunset ?? "--"}</p>
            </div>
          </div>
        </div>

        {/* Forecast Section */}
        <div className="forecast-section">
          {forecast.map((f, i) => (
            <div className="forecast-card" key={i}>
              <p>{f.day}</p>
              {f.icon}
              <p>{f.tempMin}°C / {f.tempMax}°C</p>
            </div>
          ))}
        </div>

        {/* Favorite & Remove Buttons */}
        <div className="card-buttons" style={{ display: 'flex', justifyContent: 'center', gap: 8 }}>
          <button className="favorite-btn" onClick={() => toggleFavorite(city.cityName)}>
            {city.favorite ? "★ Favorite" : "☆ Favorite"}
          </button>
          <button className="remove-btn" onClick={() => onRemove(city.cityName)}>❌ Remove</button>
        </div>
      </div>
    );
  });

  return <div className="card-container">{citylistItems}</div>;
}

/**
 * Main App
 */
function App() {
  const [cities, setCities] = useState([]);
  const [search, setSearch] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Favorites dropdown control
  const [showFavDropdown, setShowFavDropdown] = useState(false);
  const [showOnlyFavorites, setShowOnlyFavorites] = useState(false);
  const dropdownRef = useRef(null);

  // Add city (keeps your backend API)
  const addCity = () => {
    const trimmedInput = search.trim();
    if (!trimmedInput) return;

    const cityList = trimmedInput.split(",").map(c => c.trim()).filter(c => c);
    const newCityList = cityList.filter(c =>
      !cities.some(existing => (existing.cityName ?? existing.city ?? "").toLowerCase() === c.toLowerCase())
    );
    if (newCityList.length === 0) {
      setError("All cities already added or invalid input");
      return;
    }

    const query = newCityList.join(",");
    setLoading(true);
    axios.get(`http://localhost:3001/weather/cities?names=${query}`)
      .then(response => {
        if (!response.data || response.data.length === 0) {
          setError("City not found");
          return;
        }

        // assign stable color + favorite flag
        const updatedData = response.data.map(c => ({
          ...c,
          favorite: false,
          color: RandomColor()
        }));

        setCities(prev => [...prev, ...updatedData]);
        setSearch("");
        setError("");
      })
      .catch(err => {
        console.error("Error fetching cities:", err);
        setError(err.response?.data?.message || "Error fetching cities");
      })
      .finally(() => setLoading(false));
  };

  // Remove city
  const removeCity = (cityName) => {
    setCities(prev => prev.filter(city => (city.cityName ?? city.city) !== cityName));
  };

  // Toggle favorite
  const toggleFavorite = (cityName) => {
    setCities(prev => prev.map(city => {
      const name = city.cityName ?? city.city;
      return name === cityName ? { ...city, favorite: !city.favorite } : city;
    }));
  };

  // Auto-refresh: preserve favorite and color
  useEffect(() => {
    const interval = setInterval(() => {
      if (cities.length > 0) {
        const query = cities.map(c => (c.cityName ?? c.city)).join(",");
        axios.get(`http://localhost:3001/weather/cities?names=${query}`)
          .then(response => {
            if (response.data) {
              const refreshedData = response.data.map(c => {
                const oldCity = cities.find(old => (old.cityName ?? old.city) === (c.cityName ?? c.city));
                return {
                  ...c,
                  favorite: oldCity?.favorite || false,
                  color: oldCity?.color || RandomColor()
                };
              });
              setCities(refreshedData);
            }
          })
          .catch(err => console.error("Auto-refresh failed:", err));
      }
    }, 300000); // 5 minutes

    return () => clearInterval(interval);
  }, [cities]);

  // Favorites derived
  const favoriteCities = cities.filter(c => c.favorite);
  const normalCities = cities.filter(c => !c.favorite);

  // For click-outside behaviour on dropdown
  useEffect(() => {
    function handleClickOutside(event) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowFavDropdown(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => document.removeEventListener("mousedown", handleClickOutside);
  }, [dropdownRef]);

  // Dropdown actions
  const markAllFavorite = () => setCities(prev => prev.map(c => ({ ...c, favorite: true })));
  const clearFavorites = () => setCities(prev => prev.map(c => ({ ...c, favorite: false })));
  const copyFavoritesToClipboard = () => {
    const text = favoriteCities.map(f => (f.cityName ?? f.city)).join(", ");
    if (navigator.clipboard) navigator.clipboard.writeText(text);
    else alert(text || "No favorites");
  };

  // Which cities to show (if showOnlyFavorites true show only favorites else show everything)
  const showFavoritesOnly = showOnlyFavorites;

  return (
    <div className="app">
      <Header />

      {/* Favorites control (near top) */}
      <div ref={dropdownRef} style={{ position: "relative", display: "flex", justifyContent: "flex-end", margin: "10px 20px 0 0" }}>
        <button
          className="favorite-btn"
          onClick={() => setShowFavDropdown(v => !v)}
          title="Favorites & options"
          onMouseEnter={() => {/* optional hover behavior */}}
        >
          ★ Favorites ({favoriteCities.length})
        </button>

        {showFavDropdown && (
          <div className="favorites-dropdown">
            <button className="fav-opt" onClick={() => { setShowOnlyFavorites(s => !s); setShowFavDropdown(false); }}>
              {showOnlyFavorites ? "Show All Cities" : "Show Only Favorites"}
            </button>
            <button className="fav-opt" onClick={() => { markAllFavorite(); setShowFavDropdown(false); }}>Mark All Favorite</button>
            <button className="fav-opt" onClick={() => { clearFavorites(); setShowFavDropdown(false); }}>Clear Favorites</button>
            <button className="fav-opt" onClick={() => { copyFavoritesToClipboard(); setShowFavDropdown(false); }}>Copy Favorites</button>
          </div>
        )}
      </div>

      {/* Search bar */}
      <div className="search-bar" style={{ marginTop: 10 }}>
        <input
          type="text"
          placeholder="Enter city or multiple cities (comma separated)"
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyPress={(e) => { if (e.key === "Enter") addCity(); }}
        />
        <button onClick={addCity} disabled={loading}>
          {loading ? <span className="loader"></span> : "Add City"}
        </button>
      </div>

      {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}

      {/* Favorites Panel (if not showing only favorites, show favorites panel first) */}
      {!showFavoritesOnly && favoriteCities.length > 0 && (
        <>
          <h3 style={{ textAlign: "center" }}>★ Favorites</h3>
          <RenderingArrayOfCityObjects cityData={favoriteCities} onRemove={removeCity} toggleFavorite={toggleFavorite} />
        </>
      )}

      {/* Normal Cities (or when showing all) */}
      {!showFavoritesOnly && (
        <RenderingArrayOfCityObjects cityData={normalCities} onRemove={removeCity} toggleFavorite={toggleFavorite} />
      )}

      {/* If showing only favorites */}
      {showFavoritesOnly && favoriteCities.length === 0 && (
        <p style={{ textAlign: "center", color: "#fff" }}>No favorites yet</p>
      )}
      {showFavoritesOnly && favoriteCities.length > 0 && (
        <RenderingArrayOfCityObjects cityData={favoriteCities} onRemove={removeCity} toggleFavorite={toggleFavorite} />
      )}

      <Footer />
    </div>
  );
}

export default App;
