import React, { useState } from "react";
import "./App.css";

// Your OpenWeather API key
const API_KEY = "b81b6799c2282dbb9792e9d52e8d2101";

function App() {
  const [city, setCity] = useState("");
  const [weatherData, setWeatherData] = useState([]);
  const [favorites, setFavorites] = useState([]);

  // Fetch weather by city name
  const fetchWeather = async () => {
    if (!city) return;
    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${city}&appid=${API_KEY}&units=metric`
      );
      const data = await response.json();
      if (data.cod === 200) {
        setWeatherData([...weatherData, data]);
        setCity("");
      } else {
        alert("City not found!");
      }
    } catch (error) {
      console.error("Error fetching weather data:", error);
    }
  };

  // Fetch weather by device location (lat, lon)
  const fetchWeatherByCoords = async (lat, lon) => {
    try {
      const response = await fetch(
        `https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${API_KEY}&units=metric`
      );
      const data = await response.json();
      if (data.cod === 200) {
        setWeatherData([...weatherData, data]);
      } else {
        alert("Failed to fetch weather for your location.");
      }
    } catch (error) {
      console.error("Error fetching weather by coords:", error);
    }
  };

  // Handle Detect Location button
  const detectLocation = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (pos) => {
          const { latitude, longitude } = pos.coords;
          fetchWeatherByCoords(latitude, longitude);
        },
        (err) => {
          console.error(err);
          alert("Location access denied or unavailable.");
        }
      );
    } else {
      alert("Geolocation not supported by this browser.");
    }
  };

  // Add to favorites
  const addToFavorites = (city) => {
    if (!favorites.includes(city)) {
      setFavorites([...favorites, city]);
    }
  };

  return (
    <div className="App">
      <h1>🌤 Weather App</h1>

      {/* Search Bar */}
      <div className="search-bar">
        <input
          type="text"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          placeholder="Enter city"
        />
        <button onClick={fetchWeather}>Search</button>
        <button onClick={detectLocation}>📍 Detect Location</button>
      </div>

      {/* Favorites */}
      {favorites.length > 0 && (
        <div className="favorites">
          <h3>⭐ Favorites</h3>
          {favorites.map((fav, i) => (
            <span key={i} className="favorite-item">
              {fav}
            </span>
          ))}
        </div>
      )}

      {/* Weather Cards */}
      <div className="weather-container">
        {weatherData.map((data, index) => (
          <div key={index} className="weather-card">
            <h2>{data.name}</h2>
            <p>{data.weather[0].description}</p>
            <p>🌡 {data.main.temp}°C</p>
            <p>Feels like: {data.main.feels_like}°C</p>
            <p>💧 Humidity: {data.main.humidity}%</p>
            <p>🌬 Wind: {data.wind.speed} m/s</p>
            <button onClick={() => addToFavorites(data.name)}>⭐ Add to Favorites</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
