import React, { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [search, setSearch] = useState("");
  const [cities, setCities] = useState([]);
  const [allCities, setAllCities] = useState([]);
  const [suggestions, setSuggestions] = useState([]);

  // ✅ Fetch cities.json from Spring Boot backend
  useEffect(() => {
    fetch("http://localhost:8080/cities.json")
      .then((res) => res.json())
      .then((data) => {
        if (data && data.List) {
          setAllCities(data.List.map((city) => city.CityName));
        }
      })
      .catch((err) => console.error("Error loading cities.json:", err));
  }, []);

  // ✅ Handle input + suggestions
  const handleInputChange = (e) => {
    const value = e.target.value;
    setSearch(value);

    if (value.length > 0) {
      const filtered = allCities.filter((city) =>
        city.toLowerCase().startsWith(value.toLowerCase())
      );
      setSuggestions(filtered);
    } else {
      setSuggestions([]);
    }
  };

  // ✅ Add city card
  const handleAddCity = () => {
    if (search.trim() !== "") {
      const newCities = search.split(",").map((c) => c.trim());
      setCities([...cities, ...newCities]);
      setSearch("");
      setSuggestions([]);
    }
  };

  // ✅ Remove city card
  const handleRemoveCity = (city) => {
    setCities(cities.filter((c) => c !== city));
  };

  return (
    <div className="App">
      <h1>Weather App</h1>

      <div style={{ position: "relative", display: "inline-block" }}>
        <input
          type="text"
          value={search}
          onChange={handleInputChange}
          placeholder="Enter city or multiple cities (comma separated)"
          className="search-box"
        />
        <button onClick={handleAddCity} className="add-btn">
          Add City
        </button>

        {/* ✅ Suggestions Dropdown */}
        {suggestions.length > 0 && (
          <ul className="suggestions-dropdown">
            {suggestions.map((s, index) => (
              <li
                key={index}
                onClick={() => {
                  setSearch(s);
                  setSuggestions([]);
                }}
              >
                {s}
              </li>
            ))}
          </ul>
        )}
      </div>

      {/* ✅ City Cards */}
      <div className="cards-container">
        {cities.map((city, index) => (
          <div key={index} className="city-card">
            <h2>{city}</h2>
            <button
              onClick={() => handleRemoveCity(city)}
              className="remove-btn"
            >
              Remove
            </button>
          </div>
        ))}
      </div>

      <footer className="footer">Weather Application Team-15</footer>
    </div>
  );
}

export default App;
