import React, { useState } from "react";
import axios from "axios";
import "./index.css";

function App() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);

  const fetchWeather = async () => {
    try {
      const res = await axios.get(
        `http://localhost:3001/weather/cities?city=${city}`
      );
      if (res.data && res.data.length > 0) {
        setWeather(res.data[0]); // Take the first city data
        setForecast(res.data[0].forecast || []); // 5-day forecast
      }
    } catch (err) {
      console.error("Error fetching weather:", err);
    }
  };

  return (
    <div className="app">
      <div className="header">
        Weather Dashboard
        <div className="header-title">One City at a Time</div>
      </div>

      <div className="search-bar">
        <input
          type="text"
          value={city}
          onChange={(e) => setCity(e.target.value)}
          placeholder="Enter city name..."
        />
        <button onClick={fetchWeather}>Search</button>
      </div>

      {weather && (
        <div className="dashboard">
          {/* Current Weather */}
          <div className="current-weather">
            <h2>{weather.city}</h2>
            <p className="temp-big">{weather.temperature}Â°C</p>
            <p className="desc">
              {weather.description} | Feels like {weather.feels_like}Â°C
            </p>
          </div>

          {/* 4 Stat Cards */}
          <div className="stats-cards">
            <div className="stat-card">
              <h4>Humidity</h4>
              <p>{weather.humidity}%</p>
            </div>
            <div className="stat-card">
              <h4>Wind</h4>
              <p>{weather.wind_speed} km/h</p>
            </div>
            <div className="stat-card">
              <h4>Visibility</h4>
              <p>{weather.visibility} km</p>
            </div>
            <div className="stat-card">
              <h4>Pressure</h4>
              <p>{weather.pressure} hPa</p>
            </div>
          </div>

          {/* Forecast */}
          <div className="forecast-row">
            {forecast.map((day, i) => (
              <div className="forecast-box" key={i}>
                <p>{day.date}</p>
                <p>{day.temp}Â°C</p>
                <p>{day.desc}</p>
              </div>
            ))}
          </div>
        </div>
      )}

      <div className="footer-container">
        <footer className="footer">
          <span className="footer-text">
            Â© 2025 Weather Dashboard | Built by You ðŸš€
          </span>
        </footer>
      </div>
    </div>
  );
}

export default App;
