// App.js
import React, { useState } from "react";
import axios from "axios";
import "./index.css";
import Header from "./components/Header.jsx";
import Footer from "./components/Footer.jsx";
import {
  CiCloudOn,
  CiCloudDrizzle,
  CiSun,
} from "react-icons/ci";
import { TiLocationArrowOutline } from "react-icons/ti";

const API_KEY = "b81b6799c2282dbb9792e9d52e8d2101"; // ðŸ”‘ Your OpenWeather API key

// Map weather description â†’ icon
const getWeatherIcon = (desc) => {
  const d = desc.toLowerCase();
  if (d.includes("rain")) return <CiCloudDrizzle className="weather-icon" />;
  if (d.includes("cloud")) return <CiCloudOn className="weather-icon" />;
  if (d.includes("sun") || d.includes("clear"))
    return <CiSun className="weather-icon" />;
  return <CiCloudOn className="weather-icon" />;
};

function App() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  const fetchWeather = async (cityName) => {
    if (!cityName) return;
    setLoading(true);
    setError("");
    try {
      // Current weather
      const res = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${API_KEY}&units=metric`
      );
      setWeather(res.data);

      // Forecast (5-day, every 8th item â‰ˆ daily)
      const forecastRes = await axios.get(
        `https://api.openweathermap.org/data/2.5/forecast?q=${cityName}&appid=${API_KEY}&units=metric`
      );
      const daily = forecastRes.data.list.filter((_, i) => i % 8 === 0);
      setForecast(daily.slice(0, 5));
    } catch (err) {
      console.error("Error fetching weather:", err);
      setError("City not found. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchWeather(city);
  };

  return (
    <div className="app">
      <Header />

      {/* Search Bar */}
      <form className="search-bar" onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter a city..."
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button type="submit" disabled={loading}>
          {loading ? "Loading..." : "Search"}
        </button>
      </form>

      {/* Error */}
      {error && <p className="error">{error}</p>}

      {/* Current Weather */}
      {weather && (
        <section className="current-weather">
          {getWeatherIcon(weather.weather[0].description)}
          <h2 className="temperature">{Math.round(weather.main.temp)}Â°C</h2>
          <h3 className="city-name">
            {weather.name}, {weather.sys.country}
          </h3>
          <p className="condition">{weather.weather[0].description}</p>

          <div className="details">
            <div className="detail-card">
              <span>Humidity</span>
              <strong>{weather.main.humidity}%</strong>
            </div>
            <div className="detail-card">
              <span>Pressure</span>
              <strong>{weather.main.pressure} hPa</strong>
            </div>
            <div className="detail-card">
              <span>Wind</span>
              <strong>
                {weather.wind.speed} m/s <TiLocationArrowOutline />
              </strong>
            </div>
            <div className="detail-card">
              <span>Sunrise</span>
              <strong>
                {new Date(weather.sys.sunrise * 1000).toLocaleTimeString()}
              </strong>
            </div>
            <div className="detail-card">
              <span>Sunset</span>
              <strong>
                {new Date(weather.sys.sunset * 1000).toLocaleTimeString()}
              </strong>
            </div>
          </div>
        </section>
      )}

      {/* Forecast */}
      {forecast.length > 0 && (
        <section className="forecast">
          <h3>5-Day Forecast</h3>
          <div className="forecast-cards">
            {forecast.map((day, i) => (
              <div key={i} className="forecast-card">
                <p>{new Date(day.dt_txt).toLocaleDateString()}</p>
                {getWeatherIcon(day.weather[0].description)}
                <p>{Math.round(day.main.temp)}Â°C</p>
                <span>{day.weather[0].description}</span>
              </div>
            ))}
          </div>
        </section>
      )}

      <Footer />
    </div>
  );
}

export default App;
