import React, { useState, useEffect } from "react";
import axios from "axios";
import "./index.css";
import Header from "./components/Header.jsx";
import Footer from "./components/Footer.jsx";
import { TiLocationArrowOutline } from "react-icons/ti";
import { CiCloudOn, CiCloudDrizzle, CiSun } from "react-icons/ci";

const API_KEY = "b81b6799c2282dbb9792e9d52e8d2101"; // üîë Your OpenWeather API key

// Map description to icon
const getWeatherIcon = (desc) => {
  if (!desc) return <CiCloudOn className="weather-icon" />;
  const d = desc.toLowerCase();
  if (d.includes("rain")) return <CiCloudDrizzle className="weather-icon" />;
  if (d.includes("clear") || d.includes("sun")) return <CiSun className="weather-icon" />;
  return <CiCloudOn className="weather-icon" />;
};

function App() {
  const [city, setCity] = useState("");
  const [weather, setWeather] = useState(null);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Fetch weather by city name
  const fetchWeatherByCity = async (cityName) => {
    if (!cityName) return;
    setLoading(true);
    setError("");
    setWeather(null);
    try {
      const res = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${cityName}&appid=${API_KEY}&units=metric`
      );
      setWeather(res.data);
    } catch (err) {
      setError("City not found");
    } finally {
      setLoading(false);
    }
  };

  // Detect current location
  const detectLocationWeather = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(async (pos) => {
        const { latitude, longitude } = pos.coords;
        setLoading(true);
        setError("");
        setWeather(null);
        try {
          const res = await axios.get(
            `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${API_KEY}&units=metric`
          );
          setWeather(res.data);
        } catch (err) {
          setError("Could not fetch your location weather");
        } finally {
          setLoading(false);
        }
      });
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    fetchWeatherByCity(city.trim());
  };

  return (
    <div className="app">
      <Header />

      <form className="search-bar" onSubmit={handleSearch}>
        <input
          type="text"
          placeholder="Enter city..."
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <button type="submit" disabled={loading}>
          {loading ? "Loading..." : "Search"}
        </button>
        <button type="button" onClick={detectLocationWeather}>
          üìç Detect My Location
        </button>
      </form>

      {error && <p style={{ color: "red", textAlign: "center" }}>{error}</p>}

      {weather && !loading && (
        <div className="weather-card">
          <h2 className="city-name">
            {weather.name}, {weather.sys.country}
          </h2>
          <p className="city-time">{new Date().toLocaleTimeString()}</p>

          <div className="weather-main">
            {getWeatherIcon(weather.weather[0].description)}
            <h1 className="temp">{Math.round(weather.main.temp)}¬∞C</h1>
          </div>

          <p className="weather-desc">{weather.weather[0].description}</p>

          <div className="details-grid">
            <div className="detail-card">
              <span>Min</span> {Math.round(weather.main.temp_min)}¬∞C
            </div>
            <div className="detail-card">
              <span>Max</span> {Math.round(weather.main.temp_max)}¬∞C
            </div>
            <div className="detail-card">
              <span>Humidity</span> {weather.main.humidity}%
            </div>
            <div className="detail-card">
              <span>Wind</span> {weather.wind.speed} m/s <TiLocationArrowOutline />
            </div>
            <div className="detail-card">
              <span>Sunrise</span>{" "}
              {new Date(weather.sys.sunrise * 1000).toLocaleTimeString()}
            </div>
            <div className="detail-card">
              <span>Sunset</span>{" "}
              {new Date(weather.sys.sunset * 1000).toLocaleTimeString()}
            </div>
          </div>
        </div>
      )}

      <Footer />
    </div>
  );
}

export default App;
