import React, { useState } from "react";
import Header from "./components/Header";
import Footer from "./components/Footer";
import CityCards from "./components/CityCards";
import SearchBar from "./components/SearchBar";
import Sidebar from "./components/Sidebar";
import "./index.css";

function App() {
  const [cities, setCities] = useState([]);
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Add city
  const handleAddCity = (cityObj) => {
    setCities((prev) => {
      if (prev.some((c) => c.cityName === cityObj.cityName)) return prev;
      return [...prev, cityObj];
    });
  };

  // Remove city
  const handleRemoveCity = (name) => {
    setCities((prev) => prev.filter((c) => c.cityName !== name));
  };

  // Toggle sidebar
  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="app">
      <Header onToggleSidebar={toggleSidebar} />

      <Sidebar
        isOpen={isSidebarOpen}
        onClose={toggleSidebar}
        cities={cities}
      />

      <main className={`main-content ${isSidebarOpen ? "shrink" : ""}`}>
        <SearchBar onAddCity={handleAddCity} setError={setError} setLoading={setLoading} loading={loading} />
        {error && <p className="error-text">{error}</p>}
        <CityCards cityData={cities} onRemove={handleRemoveCity} />
      </main>

      <Footer />
    </div>
  );
}

export default App;
