import React, { useState, useEffect } from "react";
import axios from "axios";
import "./index.css";
import Header from "./components/Header.jsx";
import Footer from "./components/Footer.jsx";
import { TiLocationArrowOutline } from "react-icons/ti";
import { CiCloudOn, CiCloudDrizzle, CiSun } from "react-icons/ci";

const API_KEY = "b81b6799c2282dbb9792e9d52e8d2101"; // 🔑 Your OpenWeather API key

// Map description to icon
const getWeatherIcon = (desc) => {
  if (!desc) return <CiCloudOn className="weather-icon" />;
  const d = desc.toLowerCase();
  if (d.includes("rain")) return <CiCloudDrizzle className="weather-icon" />;
  if (d.includes("clear") || d.includes("sun")) return <CiSun className="weather-icon" />;
  if (d.includes("cloud")) return <CiCloudOn className="weather-icon" />;
  return <CiCloudOn className="weather-icon" />;
};

// Render multiple city cards
function CityCards({ cityData, onRemove }) {
  return (
    <div className="card-grid">
      {cityData.map((city, idx) => (
        <div className="weather-card" key={idx}>
          <div className="card-header">
            <h2>{city.cityName}, {city.country}</h2>
            <button className="remove-btn" onClick={() => onRemove(city.cityName)}>×</button>
          </div>
          <p className="last-updated">Updated: {city.lastUpdatedTime}</p>

          <div className="main-weather">
            {getWeatherIcon(city.description)}
            <h1>{city.temperature}°C</h1>
          </div>
          <p className="weather-desc">{city.description}</p>

          <div className="details-grid">
            <div><strong>Min:</strong> {city.minTemperature}°C</div>
            <div><strong>Max:</strong> {city.maxTemperature}°C</div>
            <div><strong>Humidity:</strong> {city.humidity}%</div>
            <div><strong>Pressure:</strong> {city.pressure} hPa</div>
            <div><strong>Wind:</strong> {city.wind} <TiLocationArrowOutline /></div>
            <div><strong>Visibility:</strong> {city.visibility}</div>
            <div><strong>Sunrise:</strong> {city.sunrise}</div>
            <div><strong>Sunset:</strong> {city.sunset}</div>
          </div>
        </div>
      ))}
    </div>
  );
}

function App() {
  const [cities, setCities] = useState([]);
  const [search, setSearch] = useState("");
  const [error, setError] = useState("");
  const [loading, setLoading] = useState(false);

  // Detect current location
  const detectLocationWeather = () => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(async (pos) => {
        const { latitude, longitude } = pos.coords;
        setLoading(true);
        setError("");
        try {
          const res = await axios.get(
            `https://api.openweathermap.org/data/2.5/weather?lat=${latitude}&lon=${longitude}&appid=${API_KEY}&units=metric`
          );
          const data = res.data;
          const cityObj = {
            cityName: data.name,
            country: data.sys.country,
            temperature: Math.round(data.main.temp),
            minTemperature: Math.round(data.main.temp_min),
            maxTemperature: Math.round(data.main.temp_max),
            description: data.weather[0].description,
            pressure: data.main.pressure,
            humidity: data.main.humidity,
            visibility: `${data.visibility / 1000} km`,
            wind: `${data.wind.speed} m/s`,
            sunrise: new Date(data.sys.sunrise * 1000).toLocaleTimeString(),
            sunset: new Date(data.sys.sunset * 1000).toLocaleTimeString(),
            lastUpdatedTime: new Date().toLocaleTimeString(),
          };

          setCities((prev) => {
            if (prev.some((c) => c.cityName === cityObj.cityName)) return prev;
            return [...prev, cityObj];
          });
        } catch (err) {
          setError("Could not fetch your current location weather");
        } finally {
          setLoading(false);
        }
      });
    }
  };

  // Search city weather
  const addCity = async () => {
    const trimmed = search.trim();
    if (!trimmed) return;

    setLoading(true);
    setError("");
    try {
      const res = await axios.get(
        `https://api.openweathermap.org/data/2.5/weather?q=${trimmed}&appid=${API_KEY}&units=metric`
      );
      const data = res.data;
      const cityObj = {
        cityName: data.name,
        country: data.sys.country,
        temperature: Math.round(data.main.temp),
        minTemperature: Math.round(data.main.temp_min),
        maxTemperature: Math.round(data.main.temp_max),
        description: data.weather[0].description,
        pressure: data.main.pressure,
        humidity: data.main.humidity,
        visibility: `${data.visibility / 1000} km`,
        wind: `${data.wind.speed} m/s`,
        sunrise: new Date(data.sys.sunrise * 1000).toLocaleTimeString(),
        sunset: new Date(data.sys.sunset * 1000).toLocaleTimeString(),
        lastUpdatedTime: new Date().toLocaleTimeString(),
      };

      setCities((prev) => {
        if (prev.some((c) => c.cityName === cityObj.cityName)) return prev;
        return [...prev, cityObj];
      });
      setSearch("");
    } catch (err) {
      setError("City not found");
    } finally {
      setLoading(false);
    }
  };

  // Remove city
  const removeCity = (name) => {
    setCities((prev) => prev.filter((c) => c.cityName !== name));
  };

  return (
    <div className="app">
      <Header />

      <div className="search-bar">
        <input
          type="text"
          placeholder="Enter city name..."
          value={search}
          onChange={(e) => setSearch(e.target.value)}
          onKeyPress={(e) => e.key === "Enter" && addCity()}
        />
        <button onClick={addCity} disabled={loading}>
          {loading ? "Loading..." : "Search"}
        </button>
        <button onClick={detectLocationWeather}>📍 Detect My Location</button>
      </div>

      {error && <p className="error-text">{error}</p>}

      <CityCards cityData={cities} onRemove={removeCity} />

      <Footer />
    </div>
  );
}

export default App;
