import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './index.css'; // CSS styling
import { RandomColor } from "./styles.js";
import Header from './components/Header.jsx';
import Footer from './components/Footer.jsx';
import { TiLocationArrowOutline } from "react-icons/ti";
import { CiCloudOn } from "react-icons/ci";

// Component to render city cards
function RenderingArrayOfCityObjects({ cityData, onRemove }) {
  const citylistItems = cityData.map((city, index) => {
    const topDivisionColour = { backgroundColor: RandomColor() };

    return (
      <div className="card" key={index}>
        <div className="top-division" style={topDivisionColour}>
          <div className='top-leftDivision'>
            <h2 className='city-name'>{city.cityName},{city.country}</h2>
            <p className='city-time'>{city.lastUpdatedTime}</p>
            <div className='weather-desc'>
              <CiCloudOn />
              <p className='weather-description'>{city.description}</p>
            </div>
          </div>
          <div className='top-rightDivision'>
            <div className='temp-content'>
              <h1 className='temp'>{city.temperature}¬∞C</h1>
              <p className='temp-min'>Temp min: {city.minTemperature}¬∞C</p>
              <p className='temp-max'>Temp max: {city.maxTemperature}¬∞C</p>
            </div>
          </div>
        </div>

        <div className="bottom-division">
          <div className='bottom-content'>
            <div className="extra-details">
              <p className='pressure'>Pressure : {city.pressure} hPa</p>
              <p className='humidity'>Humidity : {city.humidity}%</p>
              <p className='visibility'>Visibility : {city.visibility}</p>
            </div>
            <div className="wind">
              <div className='wind-content'>
                <div className='wind-icon'><TiLocationArrowOutline /></div>
                <div>
                  <p className='wind-speed'>{city.wind}</p>
                </div>
              </div>
            </div>
            <div className="sun">
              <p className='sun-rise'>Sunrise : {city.sunrise}</p>
              <p className='sun-set'>Sunset : {city.sunset}</p>
            </div>
          </div>
        </div>

        {/* Remove Button */}
        <button className="remove-btn" onClick={() => onRemove(city.cityName)}>‚ùå Remove</button>
      </div>
    );
  });

  return <div className="card-container">{citylistItems}</div>;
}

// Main App component
function App() {
  const [cities, setCities] = useState([]); // list of fetched cities
  const [search, setSearch] = useState(""); // search input
  const [error, setError] = useState(""); // error message
  const [loading, setLoading] = useState(false); // loader state

  // üîπ New states for autocomplete
  const [allCities, setAllCities] = useState([]); // all available cities from backend
  const [suggestions, setSuggestions] = useState([]); // filtered list

  // Fetch all available cities on mount
  useEffect(() => {
    axios.get("http://localhost:3001/weather/all-cities") // Make sure backend has this API
      .then(response => {
        setAllCities(response.data); // assume response is ["India", "USA", "Canada", ...]
      })
      .catch(err => console.error("Error fetching all cities:", err));
  }, []);

  // Function to fetch city weather
  const addCity = () => {
    const trimmedInput = search.trim();
    if (!trimmedInput) return;

    const cityList = trimmedInput.split(",").map(c => c.trim()).filter(c => c);

    const newCityList = cityList.filter(c =>
      !cities.some(existing => existing.cityName.toLowerCase() === c.toLowerCase())
    );

    if (newCityList.length === 0) {
      setError("All cities already added or invalid input");
      return;
    }

    const query = newCityList.join(",");

    setLoading(true);
    axios.get(`http://localhost:3001/weather/cities?names=${query}`)
      .then(response => {
        if (!response.data || response.data.length === 0) {
          setError("City not found");
          return;
        }
        setCities(prev => [...prev, ...response.data]);
        setSearch("");
        setError("");
        setSuggestions([]);
      })
      .catch(err => {
        console.error("Error fetching cities:", err);
        if (err.response && err.response.data && err.response.data.message) {
          setError(err.response.data.message);
        } else {
          setError("Error fetching cities");
        }
      })
      .finally(() => setLoading(false));
  };

  // Function to remove a city
  const removeCity = (cityName) => {
    setCities(prev => prev.filter(city => city.cityName !== cityName));
  };

  // Auto-refresh every 5 minutes
  useEffect(() => {
    const interval = setInterval(() => {
      if (cities.length > 0) {
        const query = cities.map(c => c.cityName).join(",");
        axios.get(`http://localhost:3001/weather/cities?names=${query}`)
          .then(response => {
            if (response.data) {
              setCities(response.data);
            }
          })
          .catch(err => console.error("Auto-refresh failed:", err));
      }
    }, 300000); // 5 minutes

    return () => clearInterval(interval);
  }, [cities]);

  return (
    <div className="app">
      <Header />

      {/* Search bar with autocomplete */}
      <div className="search-bar" style={{ position: "relative" }}>
        <input
          type="text"
          placeholder="Enter city or multiple cities (comma separated)"
          value={search}
          onChange={(e) => {
            const value = e.target.value;
            setSearch(value);

            if (value.length > 0) {
              const filtered = allCities.filter(city =>
                city.toLowerCase().startsWith(value.toLowerCase())
              );
              setSuggestions(filtered);
            } else {
              setSuggestions([]);
            }
          }}
          onKeyPress={(e) => { if (e.key === "Enter") addCity(); }}
        />
        <button onClick={addCity} disabled={loading}>
          {loading ? "Loading..." : "Add City"}
        </button>

        {/* Suggestion dropdown */}
        {suggestions.length > 0 && (
          <ul className="suggestion-box">
            {suggestions.map((city, i) => (
              <li
                key={i}
                onClick={() => {
                  setSearch(city);
                  setSuggestions([]);
                }}
              >
                {city}
              </li>
            ))}
          </ul>
        )}
      </div>

      {error && <p style={{ color: "red" }}>{error}</p>}

      {/* Loader */}
      {loading && <p style={{ color: "blue" }}>Fetching weather...</p>}

      {/* Render all city cards dynamically */}
      <RenderingArrayOfCityObjects cityData={cities} onRemove={removeCity} />

      <Footer />
    </div>
  );
}

export default App;
